let modalTitle = document.querySelector("#pokemonModalLabel"),
  modalBody = document.querySelector(".modal-body");
function editModalContent(e) {
  (modalTitle.innerText = e.name),
    (modalBody.innerHTML = `<p>ðŸ§¨ Height: ${e.height} m</p> \n  <p>ðŸ’£ Weight: ${e.weight} kg</p>\n   <p>ðŸ”‹ Type: ${e.type}</p>\n   <img src=${e.imageUrl} alt="${e.name}" />\n   <img src=${e.imgUrl} alt=${e.name} />`);
}
function addListItem(e) {
  let t = document.querySelector(".list-group"),
    n = document.createElement("li");
  n.classList.add("group-list-item", "col-lg-4", "col-sm-12", "col-md-6");
  let o = document.createElement("button");
  o.addEventListener("click", () => editModalContent(e)),
    (o.innerText = e.name),
    o.classList.add("btn", "btn-danger", "pokemon-button"),
    o.setAttribute("data-toggle", "modal"),
    o.setAttribute("data-target", "#pokemonModal"),
    n.appendChild(o),
    t.appendChild(n);
}
